import React, { useEffect, useState } from "react";

export default function Book(props) {
  const [book, setBook] = useState({});
  useEffect(() => {
    setBook(props.book);
  }, [props.book]);
  function handleBookDelete() {
    props.deleteBook(props.index);
  }

  function handleBookEdit() {
    props.editBook(props.index);
  }
  return (
    <div>
      <p>Book Id: {props.book.id}</p>
      <p>Book Name: {props.book.name}</p>
      <p>Book Price: ${props.book.price}</p>
      <button onClick={handleBookDelete}>Delete</button>
      <button onClick={handleBookEdit}>Edit</button>
      <p>---------------------------------------------</p>
    </div>
  );
}
