import React, { useEffect } from "react";
import { useState } from "react";
import CreateBook from "./CreateBook";
import Book from "./Book";
import axios from "axios";

export default function ListBooks() {
  const [books, setBooks] = useState([]);

  const url = "http://localhost:8085/";

  function getAllBooks() {
    axios
      .get(url + "books")
      .then((res) => {
        setBooks(res.data);
      })
      .catch((err) => console.log(err));
  }

  useEffect(() => {
    getAllBooks();
  }, [books]);

  function addBook(book) {
    axios
      .post(url + "books", book)
      .then((res) => console.log(res.status))
      .catch((err) => console.log(err));

    setBooks((prevBooks) => {
      return [...prevBooks, book];
    });
  }

  function deleteBook(index) {
    console.log("Delete called on Book with Id:" + index);
    let id = books[index].id;
    axios
      .delete(url + "book/" + id)
      .then((res) => console.log(res.status))
      .catch((err) => console.log(err));
    setBooks((prevBooks) => {
      return prevBooks.filter((book, idx) => index != idx);
    });
  }
  //DeleteBook using Async-Await
  // async function deleteBook(index) {
  //   console.log("Delete called on Book with Id:" + index);
  //   console.log("Async-Await!!");
  //   let id = books[index].id;
  //   try {
  //     const res = await axios.delete(url + "/" + id);
  //     console.log(res.status);
  //   } catch (error) {
  //     console.log(error);
  //   }
  //   setBooks((prevBooks) => {
  //     return prevBooks.filter((book, idx) => index != idx);
  //   });
  // }

  function editBook(index, newBook) {
    let id = books[index].id;
    axios
      .put(url + "book/" + id, { id: id, ...newBook })
      .then((res) => console.log(res.status))
      .catch((err) => console.log(err));
    setBooks((prevBooks) => {
      const newBooks = [...prevBooks];
      newBooks[index].name = newBook.name;
      newBooks[index].price = newBook.price;
      return newBooks;
    });
  }
  return (
    <div class="container">
      <div class="table-wrapper">
        <div class="table-title">
          <div class="row">
            <div class="col-sm-8">
              <h2>
                Employee <b>Details</b>
              </h2>
            </div>
            <div class="col-sm-4">
              <button type="button" class="btn btn-info add-new">
                <i class="fa fa-plus"></i> Add New
              </button>
            </div>
          </div>
        </div>
        <table class="table table-bordered">
          <thead>
            <tr>
              <th>Name</th>
              <th>Department</th>
              <th>Phone</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>John Doe</td>
              <td>Administration</td>
              <td>(171) 555-2222</td>
              <td>
                <a class="add" title="Add" data-toggle="tooltip">
                  <i class="material-icons">&#xE03B;</i>
                </a>
                <a class="edit" title="Edit" data-toggle="tooltip">
                  <i class="material-icons">&#xE254;</i>
                </a>
                <a class="delete" title="Delete" data-toggle="tooltip">
                  <i class="material-icons">&#xE872;</i>
                </a>
              </td>
            </tr>
            <tr>
              <td>Peter Parker</td>
              <td>Customer Service</td>
              <td>(313) 555-5735</td>
              <td>
                <a class="add" title="Add" data-toggle="tooltip">
                  <i class="material-icons">&#xE03B;</i>
                </a>
                <a class="edit" title="Edit" data-toggle="tooltip">
                  <i class="material-icons">&#xE254;</i>
                </a>
                <a class="delete" title="Delete" data-toggle="tooltip">
                  <i class="material-icons">&#xE872;</i>
                </a>
              </td>
            </tr>
            <tr>
              <td>Fran Wilson</td>
              <td>Human Resources</td>
              <td>(503) 555-9931</td>
              <td>
                <a class="add" title="Add" data-toggle="tooltip">
                  <i class="material-icons">&#xE03B;</i>
                </a>
                <a class="edit" title="Edit" data-toggle="tooltip">
                  <i class="material-icons">&#xE254;</i>
                </a>
                <a class="delete" title="Delete" data-toggle="tooltip">
                  <i class="material-icons">&#xE872;</i>
                </a>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
}

{
  /* <div>
      <CreateBook addBook={(book) => addBook(book)} />
      {books.length > 0 ? (
        <>
          <p>Book List:-</p>
          <div>
            {books.map((book, index) => {
              return (
                <Book
                  index={index}
                  book={book}
                  deleteBook={deleteBook}
                  editBook={editBook}
                />
              );
            })}
          </div>
        </>
      ) : (
        <p>No Books to show!</p>
      )}
    </div> */
}
