import React from "react";
import { useState } from "react";
import CreateBook from "./CreateBook";
import Book from "./Book";

export default function ListBooks() {
  const [books, setBooks] = useState([]);
  function addBook(book) {
    setBooks((prevBooks) => {
      return [...prevBooks, book];
    });
  }

  function deleteBook(index) {
    console.log("Delete called on Book with Id:" + index);
    setBooks((prevBooks) => {
      return prevBooks.filter((book, idx) => index != idx);
    });
  }

  function editBook(index) {
    let bkname = promt("Enter Book Name:");
    let bkprice = promt("Enter Book Price:");
    setBooks((prevBooks) => {
      prevBooks[index].name = bkname;
      prevBooks[index].price = bkprice;
    });
  }
  return (
    <div>
      <CreateBook addBook={(book) => addBook(book)} />
      {books.length > 0 ? (
        <>
          <p>Book List:-</p>
          <div>
            {books.map((book, index) => {
              return (
                <Book
                  index={index}
                  book={book}
                  deleteBook={deleteBook}
                  editBook={editBook}
                />
              );
            })}
          </div>
        </>
      ) : (
        <p>No Books to show!</p>
      )}
    </div>
  );
}
