import React, { useState } from "react";
import EditBook from "./EditBook";
import "./index.css";

export default function Book(props) {
  const [book, setBook] = useState({});
  const [show, setShow] = useState(true);
  function handleBookDelete() {
    props.deleteBook(props.index);
  }

  function handleBookEdit(e) {
    if (show) handleSubmit(e);
    setShow((prevShow) => !prevShow);
  }

  function handleChange(e) {
    const name = e.target.name;
    const value = e.target.value;
    setBook({ ...book, [name]: value });
    console.log(book);
  }

  function handleSubmit(e) {
    e.preventDefault();
    props.editBook(book);
  }
  // function handleBookEdit() {
  //   console.log("Edit button is pressed!");
  //   setShow(true);
  // }

  // function closeEditBook() {
  //   console.log("Closed the EditBook!");
  //   setShow(false);
  // }
  return (
    <div>
      <tr>
        {show ? (
          <>
            <td>
              <input
                type="number"
                class="form-control"
                id="bookId"
                name="id"
                onChange={handleChange}
                placeholder="Enter Book Id"
              />
            </td>
            <td>
              <input
                type="text"
                class="form-control"
                id="bookName"
                name="name"
                onChange={handleChange}
                placeholder="Enter Book Name"
              />
            </td>
            <td>
              <input
                type="text"
                class="form-control"
                id="bookPrice"
                name="price"
                onChange={handleChange}
                placeholder="Enter Book Price"
              />
            </td>
            <td>
              <a
                className="add"
                title="Add"
                data-toggle="tooltip"
                onClick={handleBookEdit}
              >
                <i className="material-icons">&#xE03B;</i>
              </a>

              <a
                className="delete"
                title="Delete"
                data-toggle="tooltip"
                onClick={handleBookDelete}
              >
                <i className="material-icons">&#xE872;</i>
              </a>
            </td>
          </>
        ) : (
          <>
            <td>{props.book.id}</td>
            <td>{props.book.name}</td>
            <td>{props.book.price}</td>
            <td>
              <a
                className="edit"
                title="Edit"
                data-toggle="tooltip"
                onClick={handleBookEdit}
              >
                <i className="material-icons">&#xE254;</i>
              </a>
              <a
                className="delete"
                title="Delete"
                data-toggle="tooltip"
                onClick={handleBookDelete}
              >
                <i className="material-icons">&#xE872;</i>
              </a>
            </td>
          </>
        )}
      </tr>
    </div>
  );
}
