import React, { useEffect } from "react";
import { useState } from "react";
import CreateBook from "./CreateBook";
import Book from "./Book";
import axios from "axios";

export default function ListBooks() {
  const [books, setBooks] = useState([]);

  const url = "http://localhost:5000/books";

  function getAllBooks() {
    axios.get(url).then((res) => {
      setBooks(res.data);
    });
  }

  useEffect(() => {
    getAllBooks();
  }, [books]);

  function addBook(book) {
    axios.post(url, book).then((res) => console.log(res.status));
    setBooks((prevBooks) => {
      return [...prevBooks, book];
    });
  }

  function deleteBook(index) {
    console.log("Delete called on Book with Id:" + index);
    let id = books[index].id;
    axios.delete(url + "/" + id).then((res) => console.log(res.status));
    setBooks((prevBooks) => {
      return prevBooks.filter((book, idx) => index != idx);
    });
  }

  function editBook(index, newBook) {
    let id = books[index].id;
    axios
      .put(url + "/" + id, { id: id, ...newBook })
      .then((res) => console.log(res.status));
    setBooks((prevBooks) => {
      const newBooks = [...prevBooks];
      newBooks[index].name = newBook.name;
      newBooks[index].price = newBook.price;
      return newBooks;
    });
  }
  return (
    <div>
      <CreateBook addBook={(book) => addBook(book)} />
      {books.length > 0 ? (
        <>
          <p>Book List:-</p>
          <div>
            {books.map((book, index) => {
              return (
                <Book
                  index={index}
                  book={book}
                  deleteBook={deleteBook}
                  editBook={editBook}
                />
              );
            })}
          </div>
        </>
      ) : (
        <p>No Books to show!</p>
      )}
    </div>
  );
}
