import React, { useEffect } from "react";
import { useState } from "react";
import CreateBook from "./CreateBook";
import Book from "./Book";
import axios from "axios";
import "./index.css";

export default function ListBooks() {
  const [books, setBooks] = useState([]);
  const [currId, setCurrId] = useState(1);
  const url = "http://localhost:8085/";

  function getAllBooks() {
    axios
      .get(url + "books")
      .then((res) => {
        setBooks(res.data);
      })
      .catch((err) => console.log(err));
  }

  useEffect(() => {
    getAllBooks();
  }, [books]);

  function addBook(book) {
    book.id = currId;
    axios
      .post(url + "books", book)
      .then((res) => {
        console.log(res.status);
        setCurrId((id) => id + 1);
      })
      .catch((err) => console.log(err));

    setBooks((prevBooks) => {
      return [...prevBooks, book];
    });
  }

  function deleteBook(index) {
    console.log("Delete called on Book with Id:" + index);
    let id = books[index].id;
    axios
      .delete(url + "book/" + id)
      .then((res) => {
        console.log(res.status);
      })
      .catch((err) => console.log(err));
    setBooks((prevBooks) => {
      return prevBooks.filter((book, idx) => index != idx);
    });
  }
  //DeleteBook using Async-Await
  // async function deleteBook(index) {
  //   console.log("Delete called on Book with Id:" + index);
  //   console.log("Async-Await!!");
  //   let id = books[index].id;
  //   try {
  //     const res = await axios.delete(url + "/" + id);
  //     console.log(res.status);
  //   } catch (error) {
  //     console.log(error);
  //   }
  //   setBooks((prevBooks) => {
  //     return prevBooks.filter((book, idx) => index != idx);
  //   });
  // }

  function editBook(index, newBook) {
    let id = books[index].id;
    axios
      .put(url + "book/" + id, { id: id, ...newBook })
      .then((res) => console.log(res.status))
      .catch((err) => console.log(err));
    setBooks((prevBooks) => {
      const newBooks = [...prevBooks];
      newBooks[index].name = newBook.name;
      newBooks[index].price = newBook.price;
      return newBooks;
    });
  }

  return (
    <div>
      <div className="container">
        <div className="table-wrapper">
          <CreateBook addBook={(book) => addBook(book)} />
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Id</th>
                <th>Name</th>
                <th>Price</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {books.length > 0 ? (
                <>
                  {books.map((book, index) => {
                    return (
                      <Book
                        index={index}
                        book={book}
                        deleteBook={deleteBook}
                        editBook={editBook}
                      />
                    );
                  })}
                </>
              ) : (
                <p>No Books to show!</p>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
